import groovy.sql.*
import java.sql.*

System.out.println("Initializing database");
def sql = Sql.newInstance("jdbc:derby:memory:glue;create=true", new Properties());
sql.execute("CREATE TABLE orders (orderIdTf varchar(256) PRIMARY KEY, orderIdFp varchar(256))");
sql.execute("CREATE TABLE registeredUsersCatalog (userName varchar(50) PRIMARY KEY, streetName varchar(256), zipCode varchar(10), cityName varchar(50), countryId varchar(20))");
System.out.println("Database initialized");

return "initialized";

