import groovy.sql.*
import java.sql.*
import java.lang.*
import fr.polytech.unice.soa1.mister.discount.business.Address

def sql = Sql.newInstance("jdbc:derby:memory:glue", new Properties());
def query = "select userName, streetName, zipCode, cityName, countryId from registeredUsersCatalog";
def data = sql.rows(query);

def addresses = new ArrayList<Address>();

for(int i = 0; i<data.size(); i++){
	def address = new Address(data[i]['userName'], data[i]['streetName'], data[i]['zipCode'], data[i]['cityName'], data[i]['countryId']);
	addresses.add(address);
}

return addresses;

import groovy.sql.*
